import React, { useState } from "react";
import { useNavigate } from "react-router-dom"; // useNavigate 불러오기;
import axios from "axios"


function PostCreateView(onPostAdded) {
  const navigate = useNavigate(); // useNavigate 훅 사용
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const [author, setAuthor] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    const postData = {
      title: title,
      content: content,
      author: author,
    };

    // 게시물 작성 로직 추가 (axios 등을 사용하여 서버에 요청)

    axios
      .post("/api/posts", postData)
      .then((response) => {
        alert("게시물이 성공적으로 작성되었습니다.");
        // 부모 컴포넌트로 콜백을 호출하여 게시글이 추가되었음을 알립니다.
        onPostAdded();
        // 필요한 경우, 추가적인 로직 (예: 페이지 새로고침 또는 목록 페이지로 리다이렉션)을 여기에 추가합니다.
      })
      .catch((error) => {
        alert("게시물 작성 중 오류가 발생했습니다.");
      });
    alert("게시물이 성공적으로 작성되었습니다.");

    // 페이지 이동
    navigate("/");
  };

  return (
    <div className="instagram-style-container">
      <h1 className="instagram-style-heading">게시물 작성</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label>제목</label>
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
          />
        </div>
        <div>
          <label>내용</label>
          <textarea
            value={content}
            onChange={(e) => setContent(e.target.value)}
          ></textarea>
        </div>
        <div>
          <label>작성자</label>
          <input
            type="text"
            value={author}
            onChange={(e) => setAuthor(e.target.value)}
          />
        </div>
        <button type="submit">게시물 작성</button>
      </form>
      <button className="instagram-button" onClick={() => navigate("/")}>
        게시글 목록
      </button>
    </div>
  );
}

export default PostCreateView;
