import React, { useState, useEffect } from "react";
import axios from "axios";
import PostForm from "./component/PostForm"; // 수정 폼 컴포넌트 추가

function App() {
  const [posts, setPosts] = useState([]);
  const [editingPost, setEditingPost] = useState(null); // 수정 중인 게시물

  useEffect(() => {
    axios.get('/api/posts')
        .then(res => setPosts(res.data))
        .catch(err => console.log(err))
  }, []);

  const handleEditClick = (post) => {
    setEditingPost(post); // 수정 버튼 클릭 시 해당 게시물 정보 저장
  };

  const handleDeleteClick = (postId) => {
    axios.delete(`/api/posts/delete/${postId}`)
      .then(() => {
        // 삭제 성공 시 게시물 목록 다시 조회
        axios.get('/api/posts')
          .then(res => setPosts(res.data))
          .catch(err => console.log(err))
      })
      .catch(err => console.log(err));
  };

  return (
      <div>
        <h1>게시물 목록</h1>
        <ul>
          {posts.map(post => (
            <li key={post.id}>
              <h2>{post.title}</h2>
              <p>{post.content}</p>
              <p>작성자: {post.author}</p>
              <button onClick={() => handleEditClick(post)}>수정</button>
              <button onClick={() => handleDeleteClick(post.id)}>삭제</button>
            </li>
          ))}
        </ul>
        {editingPost && (
          <PostForm
            post={editingPost}
            onCancel={() => setEditingPost(null)} // 수정 폼 취소 시 상태 업데이트
          />
        )}
      </div>
  );
}

export default App;
